<script setup>
const lenis = useNuxtApp().$lenis;

const scrollToTop = () => {
  lenis.scrollTo(0, { force: true });
};

const nuxtApp = useNuxtApp();

const el = useTemplateRef('el');

let ctx;
let ctxMotionFooter;

const motionFooter = () => {
  ctxMotionFooter = gsap.context(() => {
    ctxMotionFooter?.revert();

    if (IS_MOBILE.value) return;

    const tl = gsap.timeline({
      defaults: {
        ease: 'none',
      },
      scrollTrigger: {
        trigger: el.value,
        start: 'top bottom',
        end: 'bottom bottom',
        invalidateOnRefresh: true,
        scrub: true,
        refreshPriority: -1,
      },
    });

    tl.set(el.value, {
      yPercent: -50,
    });

    tl.to(el.value, {
      yPercent: 0,
    });
  }, el.value);
};

onMounted(() => {
  ctx = gsap.context(() => {
    callOnce(() => {
      motionFooter();
    });

    nuxtApp.hook('page:transition:finish', () => {
      motionFooter();
    });
  }, el.value);
});

onUnmounted(() => {
  ctxMotionFooter?.revert();
  ctx?.revert();
});
</script>

